            doc.setDrawColor(...borderColor);
            doc.line(margin + 5, 60, pageWidth - margin - 5, 60);

            const details = [
                ['Date', new Date().toLocaleDateString()],
                ['Total Staff', String(data.length - 1)]
            ];
            let y = 70;
