                        $message.css({'background': 'var(--error-bg)', 'color': 'var(--error-text)'}).text('Error: ' + (response.data?.message || 'Unknown error'));
                        setTimeout(() => $message.text(''), 4000);
                    }
                }
            });
        });
    });
    </script>
    <?php
    return ob_get_clean();
}

