        }
    }

    $center = get_posts([
        'post_type' => 'educational-center',
        'meta_key' => 'educational_center_id',
        'meta_value' => $exam->education_center_id,
        'posts_per_page' => 1
    ]);
    $institute_name = $center ? $center[0]->post_title : 'Unknown Center';
    $logo = get_field('institute_logo', $center[0]->ID ?? 0);
