        header('Content-Length: ' . strlen($pdf_content));
        header('Cache-Control: no-cache');

        echo $pdf_content;
        flush();
        exit;
    } catch (Exception $e) {
        error_log('Dompdf Error: ' . $e->getMessage());
        wp_die('Error generating PDF: ' . $e->getMessage());
    }
}

