            <?php wp_nonce_field('su_p_library_add', 'nonce'); ?>
            <input type="hidden" name="su_p_add_library_book" value="1">
            <p class="submit"><button type="submit" id="save-book" class="button-primary" disabled>Add Book</button></p>
        </form>
