                type: 'POST',
                data: formData,
                dataType: 'json', // Expect JSON response
                success: function(response) {
                    hideLoader();
                    if (response.success) {
                        alert('Timetable slot updated successfully');
                        closeModal('#edit-timetable-modal');
                        location.reload(); // Refresh table
                    } else {
