    // Fetch all timetable slots
    $query = "SELECT t.*, c.class_name, s.subject_name 
              FROM $table_name t 
              JOIN $class_table c ON t.class_id = c.id 
              LEFT JOIN $subject_table s ON t.subject_id = s.subject_id 
