                                        <td>" . esc_html($parent->parent_id) . "</td>
                                        <td>" . esc_html($parent->parent_name) . "</td>
                                        <td>" . esc_html($parent->parent_email) . "</td>
                                        <td>" . esc_html($parent->parent_phone_number) . "</td>
