                    hideLoader();
                    const $message = $('#edit-subscription-message');
                    if (response.success) {
                        $message.css({'background': 'var(--success-bg)', 'color': 'var(--success-text)'}).text(response.data);
                        $('#edit-form-container').hide();
                        loadSubscriptions();
                        setTimeout(() => $message.text(''), 4000);
