                $.ajax({
                    url: ajaxUrl,
                    method: 'POST',
                    data: { action: 'enigma_delete_subscription_plan', plan_id: planId, nonce: ajaxNonce },
                    success: function(response) {
                        hideLoader();
                        if (response.success) {
