                        }
                    },
                    error: function() {
                        hideLoader();
                        alert('Error: AJAX request failed.');
                    }
                });
            }
        });

        $('#refresh-subscription-plans').on('click', function() {
            loadSubscriptionPlans();
        });
    });
    </script>
